<?phpnamespace App\Http\Requests;use Illuminate\Foundation\Http\FormRequest;/** * Created by PhpStorm. * User: yanrong * Date: 2017/7/28 * Time: 上午10:05 */class Request extends FormRequest{    public function authorize()    {        return true;    }    public function wantsJson()    {        return true;    }    /**     * 请求不同的控制器方法，配置不同的验证规则     * @var array     */    protected $ruleConfig = [        //controller's method name => rules    ];    /**     * Get the validation rules that apply to the request.     *     * @return array     */    public function rules()    {        $routeAction = app('router')->currentRouteAction();        $method = trim(strrchr($routeAction, '@'), '@');        $rules = [];        // 是否有对应方法的验证规则        if (isset($this->ruleConfig[$method])) {            $rules = $this->ruleConfig[$method];            // 可以设置一个默认规则列表，没有对应规则时则用默认的        } elseif (!empty($this->ruleConfig['__default'])) {            $rules = $this->ruleConfig['__default'];        }        return $rules;    }}